import axios from "axios";
import MovieCard from "components/MovieCard";
import Pagination from "components/pagination";
import { useEffect, useState } from "react";
import { MoviePage } from "types/movie";
import { BASE_URL } from "utils/requests";

function Listining() {

    const [pageNumber, setPageNumber] = useState(0);

    const [page, setPage] = useState<MoviePage>({
        content: [],
        last: true,
        totalPages: 0,
        totalElements: 0,
        size: 12,
        number: 0,
        first: true,
        numberOfElements: 0,
        empty: true
    });

    // Exceute the function (param 1) when an observed object get some action. 
    // In this case, when the pageNuber changes, the request will be done again.
    useEffect(() => {
        axios.get(`${BASE_URL}/movies?size=10&page=${pageNumber}`)
            .then(response => {
                const data = response.data as MoviePage;
                setPage(data);
                //console.log(response.data);
                //setPageNumber(data.number);
            });
    }, [pageNumber]);

    // TODO: Temp
    const movie = {
        id: 1,
        title: "The Witcher",
        score: 4.5,
        count: 2,
        image: "https://www.themoviedb.org/t/p/w533_and_h300_bestv2/jBJWaqoSCiARWtfV0GlqHrcdidd.jpg"
    }

    return (
        <>
            <Pagination />
            <div className="container">
                <div className="row">
                    {page.content.map(itemMovie =>
                        <div className="col-sm-6 col-lg-4 col-xl-3 mb-3">
                            <MovieCard movie={itemMovie} />
                        </div>
                    )}

                </div>
            </div>
        </>
    );
}

export default Listining;
